mapper.select:
/* FrontJoinDAO.selectMemberJoinCheck */
select
	count(mem_id)
from cb_member
where mem_userid = #{memUserid}

/* FrontJoinDAO.selectCheckIdCount */
select
	count(mem_id)
from cb_member
where mem_userid = #{memUserid}

mapper.insert:
/* FrontJoinDAO.insertMember */
insert into cb_member
(
	mem_userid
	, mem_email
	, mem_password
	, mem_username
	, mem_nickname
	, mem_level
	, mem_point
	, mem_homepage
	, mem_phone
	, mem_birthday
	, mem_sex
	, mem_zipcode	
	, mem_address1
	, mem_address2
	, mem_address3
	, mem_address4
	, mem_receive_email
	, mem_use_note
	, mem_receive_sms
	, mem_open_profile
	, mem_denied
	, mem_email_cert
	, mem_register_datetime
	, mem_register_ip
	, mem_lastlogin_datetime
	, mem_lastlogin_ip
	, mem_is_admin
	, mem_profile_content
	, mem_adminmemo
	, mem_following
	, mem_followed
	, mem_icon
	, mem_photo
	, mem_class
	, mem_grade
	, mem_sub_id
	, mem_temp_id
)values(
	#{memUserid}
	, #{memEmail}
	, #{memPasswordEnc}
	, #{memUsername}
	, #{memNickname}
	, #{memLevel}
	, #{memPoint}
	, #{memHomepage}
	, #{memPhone}
	, #{memBirthday}
	, #{memSex}
	, #{memZipcode}
	, #{memAddress1}
	, #{memAddress2}
	, #{memAddress3}
	, #{memAddress4}
	, #{memReceiveEmail}
	, #{memUseNote}
	, #{memReceiveSms}
	, #{memOpenProfile}
	, #{memDenied}
	, #{memEmailCert}
	, now()
	, #{memRegisterIp}
	, #{memLastloginDatetime}
	, #{memLastloginIp}
	, #{memIsAdmin}
	, #{memProfileContent}
	, #{memAdminmemo}
	, #{memFollowing}
	, #{memFollowed}
	, #{memIcon}
	, #{memPhoto}
	, #{memClass}
	, #{memGrade}
	, #{memSubId}
	, #{memTempId}
)

/* FrontJoinDAO.insertAgreeInfo */
insert into cb_member_terms_agree
(
	mem_id
	, mta_terms_of_service
	, mta_privacy_policy
	, mta_marketing
	, mta_register_datatime
	, mta_register_ip
) values (
	#{memId}
	,#{mtaTermsOfService}
	,#{mtaPrivacyPolicy}
	,#{mtaMarketing}
	,now()
	,#{memRegisterIp}
)
